import React, { useState } from 'react'
import PropTypes from 'prop-types'
import Slick from 'react-slick'
import styled, { createGlobalStyle } from 'styled-components'


// ``: 함수를 호출하는 자바스크립트 문법(Tagged templates literals)
// https://developer.mozilla.org/ko/docs/Web/JavaScript/Reference/Template_literals#tagged_templates
// func()
// func``
const Overlay = styled.div`
  position: fixed;
  z-index: 5000;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
`

const Header = styled.header`
  header: 44px;
  background: white;
  position: relative;
  padding: 0;
  text-align: center;

  // 변수명 짓는 고민을 덜 할수 있다.
  & h1 {
    margin: 0;
    font-size: 17px;
    color: #333;
    line-height: 44px;
  }

  & button {
    position: absolute;
    right: 0;
    top: 0;
    padding: 15px;
    line-height: 14px;
    cursor: pointer;
  }
`

const SlickWrapper = styled.div`
  height: calc(100% - 44px);
  background: #090909;
`

const ImgWrapper = styled.div`
  padding: 32px;
  text-align: center;

  & img {
    margin: 0 auto;
    max-height: 750px;
  }
`

const Indicator = styled.div`
  text-align: center;

  & > div {
    width: 75px;
    height: 30px;
    line-height: 30px;
    border-radius: 15px;
    background: #313131;
    display: inline-block;
    text-align: center;
    color: white;
    font-size: 15px;
  }
`

// 스타일 오버라이딩을 위한
const Global = createGlobalStyle`
  .slick-slide {
    display: inline-block;
  }
  /* position: fixed와 transform이 같이 쓰일 때의 브라우저 bug */
  .ant-card-cover {
    transform: none !important;
  }
`

const ImagesZoom = ({ images, onClose }) => {
  const [currentSlide, setCurrentSlide] = useState(0)
  return (
    <Overlay>
      {/* <Global/> 아무데나 넣어도 됨 */}
      <Global />
      <Header>
        <h1>상세 이미지</h1>
        <CloseBtn onClick={onClose}>X</CloseBtn>
      </Header>
      <SlickWrapper>
        <div>
          <Slick
            initialSlide={0}
            afterChange={(slide) => setCurrentSlide(slide)}
            infinite
            arrows={false}
            // 한번에 하나씩만 보이고 하나씩만 넘길 수 있게
            slidesToShow={1}
            slidesToScroll={1}
          >
            {images.map((v) => (
              <ImgWrapper key={v.src}>
                <img src={v.src} alt={v.src} />
              </ImgWrapper>
            ))}
          </Slick>
        </div>
      </SlickWrapper>
    </Overlay>
  )
}

ImagesZoom.propTypes = {
  images: PropTypes.arrayOf(PropTypes.object).isRequired,
  onClose: PropTypes.func.isRequired
}

export default ImagesZoom
