const initialState = {
  user: {
    isLoggedIn: false,
    user: null,
    signUpData: {},
    loginData: {},
  },
  post: {
    mainPosts: []
  }
}

const changeNickname = (data) => {
  return {
    type: 'CHANGE_NICKNAME',
    data
  }
}

// 그때 그때 액션을 만드는 action creater
changeNickname('seolran_2')
// {
//   type: 'CHANGE_NICKNAME',
//   data: 'seolran_2'
// }

// (이전상태, 액션) => return 다음상태
const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'CHANGE_NICKNAME':
      return {
        ...state,
        name: action.data
      }

  }
}

export default rootReducer
