import React from 'react'
import PropTypes from 'prop-types'
import { Card, Popover, ButtonGroup, Button } from 'antd'
import { EllipsisOutlined, HeartOutlined, MessageOutlined, RetweetOutlined } from '@ant-design/icons'
import { useSelector } from 'react-redux'

const PostCard = ({ post }) => {
  // const { me } = useSelector((state) => state.user)
  // // const id = me && me.id와 같은 문법. 옵셔널 체이닝 연산자. me가 있으면 me.id가 할당되고 없으면 undefined 할당
  // const id = me?.id

  const { id } = useSelector((state) => state.user.me?.id)

  return (
    <div>
      <Card
        cover={post.Images[0] && <PostImages images={post.Images}/>}
        actions={[
          <RetweetOutlined key="retweet" />,
          <HeartOutlined key="heart" />,
          <MessageOutlined key="commnet" />,
          <Popover key="more" content={(
            <ButtonGroup>
              {
                <Button>수정</Button>,
                <Button tyep="danger">삭제</Button>
              }
              <Button>신고</Button>
            </ButtonGroup>
          )}>
            <EllipsisOutlined />
          </Popover>
        ]}
      >
        <Image />
        <Content />
        <Buttons></Buttons>
      </Card>
      <CommentForm />
      <Comments />
    </div>
  )
}

PostCard.propTypes = {
  // shape: object 타입을 더 자세히 명시해두고 싶을 때 쓴다.
  post: PropTypes.shape({
    id: PropTypes.number,
    user: PropTypes.object,
    content: PropTypes.string,
    createdAt: PropTypes.object,
    // arrayOf(PropTypes.object): 객체들의 배열이라는 뜻
    Comments: PropTypes.arrayOf(PropTypes.object),
    Images: PropTypes.arrayOf(PropTypes.object),
  }).isRequired
}
export default PostCard
