const initialState = {
  user: {
    isLoggedIn: false,
    user: null,
    signUpData: {},
    loginData: {},
  },
  post: {
    mainPosts: []
  }
}

// 그때 그때 액션을 만드는 action creater
const login = (data) => {
  return {
    type: 'LOG_IN',
    data
  }
}

// (이전상태, 액션) => return 다음상태
const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'LOG_IN':
      return {
        ...state,
        name: action.data
      }

  }
}

export default rootReducer
