// 커스텀 훅
import { useSate, useCallback } from 'react'

export default (initialValue = null) => {
  const [value, setValue] = useSate(initialValue)
  const handler = useCallback((e) => {
    setValue(e.target.value)
  }, [])
  return [value, handler]
}
