import { all, fork, call, put, take } from 'redux-saga/effects'
import axios from 'axios'

// 서버에 요청하는 함수는 일반 함수!!!
function loginAPI() {
  return axios.post('/api/login')
}

function* logIn() {
  try {
    // effect앞에는 항상 yield를 붙인다.
    // put은 dispatch.
    yield put({
      type: 'LOG_IN_REQUEST'
    })
    yield put(
      // action 객체
      {
        type: 'LOG_IN_SUCCESS',
        data: result.data
      }
    )
    const result = yield call(loginAPI)
  } catch (err) {
    yield put({
      type: 'LOG_IN_FAILURE',
      data: err.response.data
    })
  }
}

function* watchLogin() {
  // 'LOGIN'이라는 액션이 들어오면 logIn generator 함수를 실행한다.
  // eventListner와 유사한 느낌
  yield take('LOGIN', logIn)
}

function* watchLogOut() {
  yield take('LOG_OUT')
}

function* watchAddPosts() {
  yield take('ADD_POST')
}

export default function* rootSaga() {
  // all을 1) 애들을 동시에 실행할 수 있게 해준다.
  yield all([
    // 1) fork나 call로 generator함수를 실행시켜준다
    fork(watchLogin), // fork !== call 
    fork(watchLogOut),
    fork(watchAddPosts),
  ])
}
