import { all, fork, call, put, take } from 'redux-saga/effects'

function* watchLogin() {
  yield take('LOGIN')
}

function* watchLogOut() {
  yield take('LOG_OUT')
}

function* watchAddPosts() {
  yield take('ADD_POST')
}

export default function* rootSaga() {
  yield all([
    fork(watchLogin), 
    fork(watchLogOut),
    fork(watchAddPosts),
  ])
}
